{% extends 'base.html.twig' %}

{% block body %}
    <div class=\"bg-light rounded h-100 p-4\">
        <h1 class=\"mb-4\" align=\"center\">Liste des Categorie</h1>
        {% for message in app.flashes('info') %}
            <div class=\"alert alert-success\">
                {{ message }}
            </div>

{% endfor %}
        <h6 style=\"font-weight: bold\" >Nombre des Catégorie:({{ cc.getTotalItemCount }})</h6>
        <table class=\"table\">
            <thead>
            <tr>
                <th>ID</th>
                <th>Name</th>
                <th>DELETE</th><th>UPDATE</th>
                <form action=\"{{ path('ajoutCategorie') }}\" >
                    <input type=\"submit\" value=\"AjouterCategorie\" class=\"btn btn-primary w-100 m-2\">


            </tr>
            </thead>
            <tbody>
            {% for c in cc%}
                <tr>
                    <td>{{ c.id }}</td>
                    <td>{{ c.nom }}</td>
                    <td><a href=\"{{ path('DD',{'id':c.id}) }}\"  class=\"btn btn-danger rounded-pill m-2\">DELETE</a> </td>
                    <td><a href=\"{{ path('U',{'id':c.id}) }}\" class=\"btn btn-primary rounded-pill m-2\">UPDATE</a> </td>




                </tr>
            {% endfor %}
            </tbody>
        </table>
        <button type=\"button\" class=\"btn btn-secondary\">{{ knp_pagination_render(cc) }}</button>


    </div>

{% endblock %}
{% block form %}
{% endblock %}
{% block salesRevenue %}
{% endblock %}
{% block salesChart %}
{% endblock %}
{% block messages %}
{% endblock %}
{% block table %}
{% endblock %}", "categorie/Affiche.html.twig", "C:\\Users\\21694\\Desktop\\quantech\\templates\\categorie\\Affiche.html.twig");
    }
